                .text                                                                                                                                                                                                           
                .globl  main                                                                                                                                                                                                    
        main:                                                                                                                                                                                                                   
                li      $t0,    2               # load multiplicands                                                                                                                                                            
                li      $t1,    2                                                                                                                                                                                               
                mult    $t0,    $t1             # multiply                                                                                                                                                                      
                mflo    $a0                     # load the result                                                                                                                                                               
                li      $t0,    0               # set up our loop                                                                                                                                                               
                li      $t1,    10                                                                                                                                                                                              
        # the loop takes approximately 32 cycles: 2 cycles for                                                                                                                                                                  
        # setup, and 30 cycles (3 single-cycle instructions * 10                                                                                                                                                                
        # iterations)                                                                                                                                                                                                           
        for:                                                                                                                                                                                                                    
                beq     $t0,    $t1,    done                                                                                                                                                                                    
                addi    $t0,    $t0,    1                                                                                                                                                                                       
                j       for                                                                                                                                                                                                     
        done:                                                                                                                                                                                                                   
                addi    $a0,    $a0,    1                                                                                                                                                                                       
                add     $a0,    $t0,    $a0                                                                                                                                                                                     
                li      $v0,    10                                                                                                                                                                                              
                syscall
